const delay = (ms) => new Promise(resolve => setTimeout(resolve, ms));

export const lessonPlanService = {
  async getAll() {
    await delay(300);
    return [...lessonPlans];
  },

  async getById(id) {
    await delay(200);
    const lessonPlan = lessonPlans.find(lp => lp.Id === parseInt(id));
    if (!lessonPlan) {
      throw new Error("Lesson plan not found");
    }
    return { ...lessonPlan };
  },

  async create(lessonPlanData) {
    await delay(400);
    const newLessonPlan = {
      ...lessonPlanData,
      Id: Math.max(...lessonPlans.map(lp => lp.Id), 0) + 1
    };
    lessonPlans.push(newLessonPlan);
    return { ...newLessonPlan };
  },

  async update(id, lessonPlanData) {
    await delay(300);
    const index = lessonPlans.findIndex(lp => lp.Id === parseInt(id));
    if (index === -1) {
      throw new Error("Lesson plan not found");
    }
    lessonPlans[index] = { ...lessonPlans[index], ...lessonPlanData };
    return { ...lessonPlans[index] };
  },

  async delete(id) {
    await delay(200);
    const index = lessonPlans.findIndex(lp => lp.Id === parseInt(id));
    if (index === -1) {
      throw new Error("Lesson plan not found");
    }
    lessonPlans.splice(index, 1);
    return true;
  },

  // Category management
  async getCategories() {
    await delay(200);
    return [...categories];
  },

  async updateCategory(id, categoryData) {
    await delay(300);
    const index = categories.findIndex(cat => cat.Id === parseInt(id));
    if (index === -1) {
      throw new Error("Category not found");
    }
    categories[index] = { ...categories[index], ...categoryData };
    return { ...categories[index] };
  },

  async deleteCategory(id) {
    await delay(200);
    const index = categories.findIndex(cat => cat.Id === parseInt(id));
    if (index === -1) {
      throw new Error("Category not found");
    }
    categories.splice(index, 1);
    return true;
  },

  // Bulk operations
  async updateMultiple(ids, updateData) {
    await delay(400);
    const updatedPlans = [];
    for (const id of ids) {
      const index = lessonPlans.findIndex(lp => lp.Id === parseInt(id));
      if (index !== -1) {
        lessonPlans[index] = { ...lessonPlans[index], ...updateData };
        updatedPlans.push({ ...lessonPlans[index] });
      }
    }
    return updatedPlans;
  },

  async deleteMultiple(ids) {
    await delay(300);
    const deletedIds = [];
    for (const id of ids) {
      const index = lessonPlans.findIndex(lp => lp.Id === parseInt(id));
      if (index !== -1) {
        lessonPlans.splice(index, 1);
        deletedIds.push(parseInt(id));
      }
    }
return deletedIds;
  },

  // Individual export methods
  async exportSinglePDF(id) {
    await delay(200);
    const lessonPlan = lessonPlans.find(lp => lp.Id === parseInt(id));
    if (!lessonPlan) {
      throw new Error("Lesson plan not found");
    }
    return { ...lessonPlan };
  },

  async exportSingleDOCX(id) {
    await delay(200);
    const lessonPlan = lessonPlans.find(lp => lp.Id === parseInt(id));
    if (!lessonPlan) {
      throw new Error("Lesson plan not found");
    }
    return { ...lessonPlan };
  },

  async shareLessonPlan(id) {
    await delay(200);
    const lessonPlan = lessonPlans.find(lp => lp.Id === parseInt(id));
    if (!lessonPlan) {
      throw new Error("Lesson plan not found");
    }
return { ...lessonPlan };
  },

  // Get default icon for subject and class level
  async getDefaultIcon(subject, className) {
    await delay(150);
    
    if (!subject || !className) {
      return null;
    }
    
    const subjectMappings = defaultIconsMap[subject];
    if (!subjectMappings) {
      return null;
    }
    
    const icon = subjectMappings[className];
    return icon || null;
  },

  // Get all available default icons for a subject
  async getSubjectIcons(subject) {
    await delay(150);
    
    if (!subject) {
      return {};
    }
    
    return defaultIconsMap[subject] || {};
  },

  // Get all subjects with their available class levels
  async getSubjectClassLevels() {
    await delay(150);
    
    const subjects = {};
    Object.keys(defaultIconsMap).forEach(subject => {
      subjects[subject] = Object.keys(defaultIconsMap[subject]);
    });
    
    return subjects;
  }
};